import { Component, OnInit } from "@angular/core";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { IFlashcard } from '../Flashcards/flashcard';;
import { ICollection } from '../Collections/collection';
import { ICollectionFlashcard } from './collectionFlashcard';
import { CollectionFlashcardService } from './collectionFlashcards.service';
import { FlashcardService } from '../Flashcards/flashcards.service';
import { forkJoin } from 'rxjs';

@Component({
  selector: "app-collectionsFlashcards-collectionflashcardform",
  templateUrl: "./collectionFlashcardform.component.html"
})

export class CollectionFlashcardFormComponent implements OnInit{
  collectionFlashcardForm: FormGroup;
/*  selectedFlashcardForm: FormGroup;*/
  collectionFlashcards: ICollectionFlashcard[] = [];
  flashcards: IFlashcard[] = [];
  collectionId: number = 0;

  constructor(
    private _flashcardService: FlashcardService,
    private _collectionFlashcardService: CollectionFlashcardService,
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute
  ) {
    this.collectionFlashcardForm = _formbuilder.group({
      flashcardId: [-1],
      collectionId: this.collectionId,
      collection: [''],
      flashcard: ['']

      //collectionFlashcards: [''],
      //flashcards: ['', Validators.required],
      //collectionId: this.collectionId,
    })
    //this.selectedFlashcardForm = _formbuilder.group({
    //  flashcardId: [-1],
    //  collectionId: [this.collectionId],
    //})


    // Automatic data insertion
    forkJoin([
      this._flashcardService.getFlashcards(),
    ]).subscribe(
      ([flashcards]) => {
        this.flashcards = flashcards;
        console.log("Retrieved flashcards: ", flashcards)

        
        //this.collectionFlashcardForm.patchValue({
        //  flashcardId: 
        //  collectionId: this.collectionId,
        //});
      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );
  }

  onSubmit() {
    var newCollectionFlashcards: ICollectionFlashcard[] = [];

    for (let selectedFlashcard of this.collectionFlashcards) {
      this.collectionFlashcardForm.patchValue({
        flashcardId: selectedFlashcard.flashcardId,
        collectionId: this.collectionId,
      })
      this.collectionFlashcards.push({
        flashcardId: selectedFlashcard.flashcardId,
        collectionId: this.collectionId,
      })
      this._collectionFlashcardService.addCollectionFlashcard(this.collectionFlashcardForm.value)
    }
    /*newCollectionFlashcards.push(collectionFlashcard)*/
    //console.log("Selected Flashcard: ", this.collectionFlashcardForm.value);
    ///*console.log("Created collectionFlashcard Real ", this.collectionFlashcards);*/

    //for (let flashcard of this.collectionFlashcardForm.value.flashcards) {
    //  newCollectionFlashcards.push({ flashcardId: flashcard.flashcardId, collectionId: this.collectionId })
    //  console.log("pushed collectionflashcard with flashcardId: ", flashcard.flashcardId, " and collectionId: ", this.collectionId)
    //}

    //for (let cf of newCollectionFlashcards) {
      
    //  console.log("added cf: ", cf)
    //}
    }


  private handleResponse(response: any) {
    if (response.success) {
      console.log(response.message);
      this._router.navigate(['/collections']);
    } else {
      console.log('Collection operation failed');
    }
  }

  backToCollections() {
    this._router.navigate(['/collections']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      this.collectionId = +params['id']; // Convert to number
      this._collectionFlashcardService.getCollectionFlashcard().subscribe(cf => {
        this.collectionFlashcards = cf;
      })
      })
  }



  loadCollectionFlashcardsByCollectionId(collectionId: number) {
    this._collectionFlashcardService.getCollectionFlashcardByCollectionId(collectionId)
      .subscribe(
        (collectionFlashcard: any) => {
          console.log('retrieved collectionFlashcard: ', collectionFlashcard)
          this.collectionFlashcardForm.patchValue({
            flashcardId: collectionFlashcard.flashcardId,
            collectionId: this.collectionId,
          });
        },
        (error: any) => {
          console.error('Error loading collection for edit:', error);
        }
      );
  }
}
<h2>Add Flashcard to Collection</h2>

<form [formGroup]="collectionFlashcardForm" (ngSubmit)="onSubmit()">

  <div class="form-group">
    <label for="collectionFlashcard">Add flashcards:</label>
    <br />

    <select formControlName="flashcards" multiple style="width: 500px;">
      <option *ngFor="let flashcard of flashcards" [ngValue]="flashcard">
        {{flashcard.flashcardId}}. Term: {{flashcard.name}}, Description: {{flashcard.description}}
      </option>
    </select>

    <!--<select multiple style="width: 500px;">
    <option *ngFor="let collectionflashcard of collectionFlashcards" [ngValue]="collectionflashcard">
    {{collectionflashcard.flashcard.flashcardId}}.
    Term: {{collectionflashcard.flashcard.name}},
    Description: {{collectionflashcard.flashcard.description}}</option>
  </select>-->
  </div>

  <button class="btn-primary btn" type="submit" [disabled]="collectionFlashcardForm.invalid">Add Flashcard to Collection</button>
  <button class="btn btn-secondary" (click)="backToCollections()">Cancel</button>

</form>
