import { Component, OnInit } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { CollectionService } from './collections.service';
import { IFlashcard } from '../Flashcards/flashcard';
import { FlashcardService } from '../Flashcards/flashcards.service';

// Dropdown implementation
import { IDropdownSettings } from 'ng-multiselect-dropdown'; // https://www.npmjs.com/package/ng-multiselect-dropdown
import { forEachChild } from "typescript";

@Component({
  selector: "app-collections-collectionform",
  templateUrl: "./collectionform.component.html"
})

export class CollectionformComponent implements OnInit{
  collectionForm: FormGroup;
  isEditMode: boolean = false;
  collectionId: number = -1;
  flashcards: IFlashcard[] = [];

  dropdownList: IFlashcard[] = [];
  selectedFlashcards: IFlashcard[] = [];
  dropdownSettings = {};

  constructor(private _collectionService: CollectionService, private _flashcardService: FlashcardService, private _formbuilder: FormBuilder, private _router: Router, private _route: ActivatedRoute) {
    this.collectionForm = _formbuilder.group({
      collectionName: ['', Validators.required],
      collectionDate: [''],
      totalFlashcards: [''],
      flashcards: [''],
      //collectionFlashcard: [''],
      //contributerId: [''],
      //contributers: [''],
    });

    // Automatic data insertion
    this.collectionForm.patchValue({
      collectionDate: new Date().getDate() + "." + (new Date().getMonth() + 1) + "." + new Date().getFullYear(),
      totalFlashcards: this.selectedFlashcards.length,
      flashcards: this._flashcardService.getFlashcards().subscribe(data => {
        console.log('All', data);
        this.flashcards = data;
        console.log("selected flashcards:", this.selectedFlashcards);
      }),
    })
  }

  onChange(value: any) {
    console.log("OnChange value: ", value);
  }

  onSubmit() {
    console.log("CollectionCreate form submitted:");
    console.log("CollectionForm" + this.collectionForm);
    console.log("The collection " + this.collectionForm.value.collectionName + " is created.");
    console.log("Touched? " + this.collectionForm.touched);
    console.log("Selected" + this.selectedFlashcards)
    const newCollection = this.collectionForm.value;



    if (this.isEditMode) {
      this._collectionService.updateCollection(this.collectionId, newCollection)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/collections']);
          }
          else {
            console.log('Collection update failed');
          }
        });
    }
    else {
      this._collectionService.createCollection(newCollection)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/collections']);
          }
          else {
            console.log('Collections creation failed');
          }
        });
    }
  }

  backToCollections() {
    this._router.navigate(['/collections']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false; // Create mode
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true; // Edit mode
        this.collectionId = +params['id']; // Convert to number
        this.loadCollectionForEdit(this.collectionId);
      }
    });
    this.dropdownList = [ 
      { flashcardId: 1, name: "somthign", description: "fasjdkløf", imageUrl: "asdhjfkl" },
      { flashcardId: 2, name: "somtdfasdhign", description: "fasjasdfasdfdkløf", imageUrl: "asdhjfasdfasdfkl" },
    ]

    this.dropdownSettings = {
      singleSelection: false,
      idField: 'flaschardId',
      textField: 'flashcardName',
      //selectAllText: 'Select All',
      //unSelectAllText: 'UnSelect All',
      //itemsShowLimit: 3,
      //allowSearchFilter: true
    };
  }

  loadCollectionForEdit(collectionId: number) {
    this._collectionService.getCollectionById(this.collectionId)
      .subscribe(
        (collection: any) => {
          console.log('retrived collection: ', collection);
          this.collectionForm.patchValue({
            collectionName: collection.collectionName,
            collectionDate: collection.collectionDate,
            contributer: collection.contributer,
            totalFlashcards: collection.totalFlashcards
          });
        },
        (error: any) => {
          console.error('Error loading collection for edit:', error);
        }
      );
  }
}
<h2>{{isEditMode ? 'Update' : 'Create'}} Collection</h2>

<form [formGroup]="collectionForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="collectionName">Name:</label>
    <input type="text" id="collectionName" formControlName="collectionName" class="form-control" />
  </div>
  <div class="form-group">
    <label for="collectionFlashcard">Add flashcards:</label>
    <br />
    <!--<select multiple name="selected" (formControlName)="selected" style="width: 300px;" >
    <option *ngFor="let flashcard of flashcards" [ngValue]="flashcard">{{flashcard.flashcardId}}. Term: {{flashcard.name}}, Definition: {{flashcard.description}}</option>
  </select>
                                   (onSelect)="onItemSelect($event)"
                             (onSelectAll)="onSelectAll($event)">-->
    <ng-multiselect-dropdown [placeholder]="'custom placeholder'"
                             [settings]="dropdownSettings"
                             [data]="dropdownList"
                             [(ngModel)]="selectedItems"
                             >
    </ng-multiselect-dropdown>
  </div>

  <button class="btn-primary btn" type="submit" [disabled]="collectionForm.invalid">{{isEditMode ? 'Update' : 'Create'}} Collection</button>
  <button class="btn btn-secondary" (click)="backToCollections()">Cancel</button>
</form>
