import { Component } from "@angular/core";
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { CollectionService } from './collections.service';
import { IFlashcard } from '../Flashcards/flashcard';
import { FlashcardService } from '../Flashcards/flashcards.service';
import { forkJoin } from 'rxjs';
// for dropdown
import { Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: "app-collections-collectionform",
  templateUrl: "./collectionform.component.html"
})

export class CollectionformComponent {
  collectionForm: FormGroup;
  isEditMode: boolean = false;
  collectionId: number = -1;
  flashcards: IFlashcard[] = [];

  //dropdown functionality
  checkedFlashcards: any[] = [];
  currentSelected: any = {}
  showDropdown: boolean = false;

  @Output() sharedCheckedList = new EventEmitter();
  @Output() shareIndividualCheckedList = new EventEmitter();

  constructor(
    private _collectionService: CollectionService,
    private _flashcardService: FlashcardService,
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute
  ) {
    this.collectionForm = _formbuilder.group({
      collectionName: ['', Validators.required],
      flashcards: [[]],
    });

    //dropdown functionality
    this.checkedFlashcards = [];
    this.sharedCheckedList;
    currentSelected: { };
    this.showDropdown = false;


    // Automatic data insertion
    forkJoin([
      this._flashcardService.getFlashcards(),
    ]).subscribe(
      ([flashcards]) => {
        this.flashcards = flashcards;
        this.collectionForm.patchValue({
          collectionDate: new Date().getDate() + "." + (new Date().getMonth() + 1) + "." + new Date().getFullYear(),
          flashcards: [],
        });
      },
      (error) => {
        console.error('Error fetching data:', error);
      }
    );
  }

  getSelectedValue(status: Boolean, value: String) {
    if (status) {
      this.checkedFlashcards.push(value);
    } else {
      var index = this.checkedFlashcards.indexOf(value);
      this.checkedFlashcards.splice(index, 1);
    }

    this.currentSelected = { checked: status, name: value };

    //share checked list
    this.shareCheckedlist();

    //share individual selected item
    this.shareIndividualStatus();
  }
  shareCheckedlist() {
    this.sharedCheckedList.emit(this.checkedFlashcards);
  }
  shareIndividualStatus() {
    this.shareIndividualCheckedList.emit(this.currentSelected);
  }


  //Rest of functionality
  onSubmit() {
    console.log("CollectionCreate form submitted:");
    console.log("The collection " + this.collectionForm.value.collectionName + " is created.");
    console.log("Touched? " + this.collectionForm.touched);
    console.log("Selected", this.collectionForm.value.flashcards);

    const newCollection = this.collectionForm.value;

    if (this.isEditMode) {
      this._collectionService.updateCollection(this.collectionId, newCollection)
        .subscribe(response => this.handleResponse(response));
    } else {
      this._collectionService.createCollection(newCollection)
        .subscribe(response => this.handleResponse(response));
    }
  }

  private handleResponse(response: any) {
    if (response.success) {
      console.log(response.message);
      this._router.navigate(['/collections']);
    } else {
      console.log('Collection operation failed');
    }
  }

  backToCollections() {
    this._router.navigate(['/collections']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false; // Create mode
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true; // Edit mode
        this.collectionId = +params['id']; // Convert to number
        this.loadCollectionForEdit(this.collectionId);
      }
    });
  }

  loadCollectionForEdit(collectionId: number) {
    this._collectionService.getCollectionById(collectionId)
      .subscribe(
        (collection: any) => {
          console.log('retrieved collection: ', collection);
          this.collectionForm.patchValue({
            collectionName: collection.collectionName,
            flashcards: collection.flashcards, // Assuming 'flashcards' is the property name in your collection model
          });
        },
        (error: any) => {
          console.error('Error loading collection for edit:', error);
        }
      );
  }
}
<h2>{{isEditMode ? 'Update' : 'Create'}} Collection</h2>

<form [formGroup]="collectionForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="collectionName">Name:</label>
    <input type="text" id="collectionName" formControlName="collectionName" class="form-control" />
  </div>
  <div class="form-group">
    <label for="collectionFlashcard">Add flashcards:</label>
    <br />


    <div>
      <div (mouseleave)="showDropDown = false">
        <button class="drop-toggle btn flat" (click)="showDropDown=!showDropDown">
          <span *ngIf="checkedList.length<=0">Select</span>
          <span *ngIf="checkedList.length>0">{{checkedList.join(', ')}}</span>
          <i class="fa fa-angle-down"></i>
        </button>
        <div class="drop-show" *ngIf="showDropDown">
          <label *ngFor="let a of list">
            <input type="checkbox" [(ngModel)]="a.checked"
                   (change)="getSelectedValue(a.checked,a.name)" />
            <span>{{a.name}}</span>
          </label>
        </div>
      </div>
    </div>


      <!--<ul class="dropdown">
      <li *ngFor="let flashcard of flashcards">
        <label for="{{flashcard.flashcardId}}">
          <input selected type="checkbox" id="{{flashcard.flashcardId}}" name="{{flashcard.name}}" /> {{flashcard.name}}
        </label>
      </li>
    </ul>-->
      <!--<input *ngIf="isEditMode == true" id="dropdown1" ej-dropdownlist type="checkbox"/>-->
      <!--<select multiple formControlName="flashcards" style="width: 300px;">
      <option *ngFor="let flashcard of flashcards" [ngValue]="flashcard">{{flashcard.flashcardId}}. Term: {{flashcard.name}}, Definition: {{flashcard.description}}</option>
    </select>-->
    </div>



    ri

    <button class="btn-primary btn" type="submit" [disabled]="collectionForm.invalid">{{isEditMode ? 'Update' : 'Create'}} Collection</button>
    <button class="btn btn-secondary" (click)="backToCollections()">Cancel</button>
</form>
