import { Component, OnInit } from "@angular/core";
import { FormGroup, FormBuilder, Validators, FormArray } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { CollectionService } from './collections.service';
import { IFlashcard } from '../Flashcards/flashcard';
import { FlashcardService } from '../Flashcards/flashcards.service';

@Component({
  selector: "app-collections-collectionform",
  templateUrl: "./collectionform.component.html"
})
export class CollectionformComponent implements OnInit {
  collectionForm: FormGroup;
  isEditMode: boolean = false;
  collectionId: number = -1;
  flashcards: IFlashcard[] = [];

  constructor(
    private _collectionService: CollectionService,
    private _flashcardService: FlashcardService,
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute
  ) {
    this.collectionForm = _formbuilder.group({
      collectionName: ['', Validators.required],
      collectionDate: [''],
      totalFlashcards: [''],
      flashcardCheckboxes: this._formbuilder.array([]),
    });

    this._flashcardService.getFlashcards().subscribe(data => {
      console.log('All Flashcards', JSON.stringify(data));
      this.flashcards = data;

      // Create form controls for each flashcard
      this.flashcards.forEach(() => {
        const control = this._formbuilder.control(false);
        (this.collectionForm.get('flashcardCheckboxes') as FormArray).push(control);
      });
    });

    // Subscribe to changes in flashcard checkboxes to update totalFlashcards
    this.collectionForm.get('flashcardCheckboxes').valueChanges.subscribe(checkboxes => {
      const selectedFlashcards = this.flashcards.filter((flashcard, index) => checkboxes[index]);
      this.collectionForm.patchValue({
        totalFlashcards: selectedFlashcards.length,
      });
    });
  }

  onSubmit() {
    console.log("CollectionCreate form submitted:");
    console.log(this.collectionForm);
    console.log("The collection " + this.collectionForm.value.collectionName + " is created.");

    const newCollection = this.collectionForm.value;

    if (this.isEditMode) {
      this._collectionService.updateCollection(this.collectionId, newCollection)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/collections']);
          } else {
            console.log('Collection update failed');
          }
        });
    } else {
      this._collectionService.createCollection(newCollection)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/collections']);
          } else {
            console.log('Collections creation failed');
          }
        });
    }
  }

  backToCollections() {
    this._router.navigate(['/collections']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false; // Create mode
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true; // Edit mode
        this.collectionId = +params['id']; // Convert to number
        this.loadCollectionForEdit(this.collectionId);
      }
    });
  }

  loadCollectionForEdit(collectionId: number) {
    this._collectionService.getCollectionById(this.collectionId)
      .subscribe(
        (collection: any) => {
          console.log('Retrieved collection: ', collection);
          this.collectionForm.patchValue({
            collectionName: collection.collectionName,
            collectionDate: collection.collectionDate,
          });

          // Update flashcard checkboxes based on the loaded collection's flashcards
          const selectedFlashcardIds = collection.collectionFlashcard.map(flashcard => flashcard.flashcardId);
          const checkboxControls = this.collectionForm.get('flashcardCheckboxes') as FormArray;
          selectedFlashcardIds.forEach(flashcardId => {
            const index = this.flashcards.findIndex(flashcard => flashcard.flashcardId === flashcardId);
            if (index !== -1) {
              checkboxControls.at(index).setValue(true);
            }
          });
        },
        (error: any) => {
          console.error('Error loading collection for edit:', error);
        }
      );
  }
}



