import { Component, OnInit } from '@angular/core';
import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable, of } from "rxjs";
import { ICollectionFlashcard } from "./collectionFlashcard";

@Injectable({
  providedIn: "root"
})

export class CollectionFlashcardService implements OnInit {
  private baseUrl = "api/collectionFlashcard";
  private collectionFlashcards: ICollectionFlashcard[] = [];

  constructor(private _http: HttpClient) { }

  getCollectionFlashcard(): Observable<ICollectionFlashcard[]> {
    return this._http.get<ICollectionFlashcard[]>(this.baseUrl);
  }
  getCollectionFlashcardByCollectionId(collectionId: number):void { 
    const filteredResult = this.collectionFlashcards.filter((collections: ICollectionFlashcard) => collections.collectionId == collectionId);
    this.collectionFlashcards = filteredResult
    console.log("collectionFlashcards", this.collectionFlashcards)
  }

  addCollectionFlashcard(newCollectionFlashcard: ICollectionFlashcard): Observable<any> {
    const createUrl = "api/collectionFlashcard/add";
    return this._http.post<any>(createUrl, newCollectionFlashcard);
  }

  ngOnInit() {
    this.getCollectionFlashcardByCollectionId(2);
  }
}
