import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable } from "rxjs";
import { IContributor } from "./contributor";

@Injectable({
  providedIn: "root"
})

export class ContributorService {
  private baseUrl = "api/contributor/"; 

  constructor(private _http: HttpClient) { }

  getContributors(): Observable<IContributor[]> {
    return this._http.get<IContributor[]>(this.baseUrl);
  }

  createContributor(newContributor: IContributor): Observable<any> {
    const createUrl = "api/contributor/create";
    return this._http.post<any>(createUrl, newContributor);
  }

  getContributorById(contributorId: number): Observable<any> {
    const url = `${this.baseUrl}/${contributorId}`;
    return this._http.get(url)
  }
 
  updateContributor(contributorId: number, newContributor: any): Observable<any> {
    const url = `${this.baseUrl}update/${contributorId}`;
    newContributor.contributorId = contributorId;
    return this._http.put<any>(url, newContributor);
  }

  deleteContributor(contributorId: number): Observable<any> {
    const url = `${this.baseUrl}/delete/${contributorId}`;
    return this._http.delete(url);
  }
}
