// collectioncreate.component.ts

import { Component } from "@angular/core";
import { FormBuilder, FormGroup, Validators } from "@angular/forms";
import { Router } from "@angular/router";
import { CollectionService } from './collections.service';
import { ICollection } from './collection';


@Component({
  selector: "app-collections-collectioncreate",
  templateUrl: "./collectioncreate.component.html"
})

export class CollectionCreateComponent {
  collectionForm: FormGroup;

  constructor(
    private _collectionService: CollectionService,
    private _formbuilder: FormBuilder,
    private _router: Router
  ) {
    this.collectionForm = _formbuilder.group({
      collectionName: ['', Validators.required],
      collectionId: [''],
      collectionDate: [''],
      collectionFlashcard: [''],
      totalFlashcards: [''],
      contributers: [''],
      contributerId: [''],
    });
  }

  onSubmit() {
    console.log("CollectionCreate form submitted:");
    console.log("The collection " + this.collectionForm.value.collectionName + " is created.");

    const newCollection = {
      collectionId: 0,
      collectionName: this.collectionForm.value.collectionName,
      collectionDate: '',
      collectionFlashcard: '',
      totalFlashcards: 0,
      contributers: '',
      contributerId: 0,
    };

    this._collectionService.createCollection(newCollection)
      .subscribe(response => this.handleResponse(response));
  }

  private handleResponse(response: any) {
    if (response.success) {
      console.log(response.message);
      this._router.navigate(['/collections']);
    } else {
      console.log('Collection creation failed');
    }
  }

  backToCollections() {
    this._router.navigate(['/collections']);
  }
}
<!-- collectioncreate.component.html -->

<h2>Create Collection</h2>

<form [formGroup]="collectionForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label for="collectionName">Name:</label>
    <input type="text" id="collectionName" formControlName="collectionName" class="form-control" />
  </div>
  <div class="form-group">
    <label for="collectionFlashcard">Add flashcards:</label>
    <br />
    <select multiple formControlName="flashcards" style="width: 300px;">
      <option *ngFor="let flashcard of flashcards" [ngValue]="flashcard">
        {{flashcard.flashcardId}}. Term: {{flashcard.name}}, Definition: {{flashcard.description}}
      </option>
    </select>
  </div>

  <button class="btn-primary btn" type="submit" [disabled]="collectionForm.invalid">Create Collection</button>
  <button class="btn btn-secondary" (click)="backToCollections()">Cancel</button>
</form>
